CASE WHEN CASE WHEN CURRENT_DATE + INTERVAL 'P1M' + INTERVAL 'PT12H' IS NULL THEN 'Null' WHEN pg_typeof(CURRENT_DATE + INTERVAL 'P1M' + INTERVAL 'PT12H')::text LIKE '%[]' THEN 'List' ELSE CASE pg_typeof(CURRENT_DATE + INTERVAL 'P1M' + INTERVAL 'PT12H')::text WHEN 'integer' THEN 'Int' WHEN 'bigint' THEN 'Int' WHEN 'double precision' THEN 'Float' WHEN 'numeric' THEN 'Float' WHEN 'boolean' THEN 'Bool' WHEN 'text' THEN 'String' WHEN 'character varying' THEN 'String' WHEN 'unknown' THEN 'String' WHEN 'interval' THEN 'Duration' WHEN 'date' THEN 'DateTime' WHEN 'timestamp without time zone' THEN 'DateTime' WHEN 'timestamp with time zone' THEN 'DateTime' ELSE 'Tuple' END END = 'DateTime' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
