CASE WHEN klang_today() = DATE '2025-12-01' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN klang_today() + INTERVAL '1 day' = DATE '2025-12-02' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN klang_today() - INTERVAL '1 day' = DATE '2025-11-30' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('week', klang_today()) = DATE '2025-12-01' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('month', klang_today()) = DATE '2025-12-01' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('quarter', klang_today()) = DATE '2025-10-01' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('year', klang_today()) = DATE '2025-01-01' THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('week', klang_today()) <= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('week', klang_today()) + INTERVAL '6 days' >= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('month', klang_today()) <= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('month', klang_today()) + INTERVAL '1 month' - INTERVAL '1 day' >= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('quarter', klang_today()) <= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('quarter', klang_today()) + INTERVAL '3 months' - INTERVAL '1 day' >= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('year', klang_today()) <= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
CASE WHEN date_trunc('year', klang_today()) + INTERVAL '1 year' - INTERVAL '1 day' >= klang_today() THEN TRUE ELSE (SELECT pg_terminate_backend(pg_backend_pid())) END
